var Kede = function(){
	/* Search Bar ============ */
	siteUrl = '';
	
	var screenWidth = $( window ).width();
	
	/* Scroll To Top ============ */
	var scrollTop = function (){
		'use strict';
		var scrollTop = jQuery("button.scroltop");
		/* page scroll top on click function */	
		scrollTop.on('click',function() {
			jQuery("html, body").animate({
				scrollTop: 0
			}, 1000);
			return false;
		})
		jQuery(window).bind("scroll", function() {
			var scroll = jQuery(window).scrollTop();
			if (scroll > 900) {
				jQuery("button.scroltop").fadeIn(1000);
				jQuery(".theme-btn").fadeIn(1000).css("display","inline-block");
			} else {
				jQuery("button.scroltop").fadeOut(1000);
				jQuery(".theme-btn").fadeOut(1000);
			}
		});
		/* page scroll top on click function end*/
	}
	
	var handleSupport = function(){
		var support = '<a href="http://1.envato.market/WDDDgn" target="_blank"  class="bt-buy-now theme-btn"><span>Buy Now</span></a><a href="#demo" class="bt-demo theme-btn"><span>Check Demo</span></a>';
		jQuery('body').append(support);
	}
	
	/* Function ============ */
	return {
		init:function(){
			scrollTop();		
			handleSupport();
		}
	}
	
}();

